import React from 'react'
import styled from 'styled-components'
import ThumbUpOutlinedIcon from "@mui/icons-material/ThumbUpOutlined";
import ThumbDownOffAltOutlinedIcon from "@mui/icons-material/ThumbDownOffAltOutlined";
import ReplyOutlinedIcon from "@mui/icons-material/ReplyOutlined";
import AddTaskOutlinedIcon from "@mui/icons-material/AddTaskOutlined";



const Container = styled.div`
    display:flex;
    gap: 24px;
`;

const Wrapper = styled.div`
    flex:7;
`;
const VideoPlayer = styled.div`
    
`;

const Recommendation = styled.div`
    flex:3;
   
`;

const VideoImage = styled.img`
    width: 100%;
    height:800px;
   
`;



const Details = styled.div`
display: flex;
align-items: center;
justify-content: space-between;
`;

const ChannelImage = styled.img`
    width: 100px;
    height:100px;
    border-radius: 50%; 
    background-color: #999999;
`;

const Texts = styled.div`

`;

const Title = styled.h1`
    font-size:16px;
    font-weight: 500;
    margin-top: 10px;
    margin-bottom: 20px;
    color: ${({ theme }) => theme.text};
`;

const ChannelName = styled.h2`

`;

const Info = styled.span`
  color: ${({ theme }) => theme.textSoft};
`;


const ButtonsGroup = styled.div`
    display: flex;
    gap: 20px;
    color: ${({ theme }) => theme.text};

`;

const Button = styled.div`
    display: flex; 
    align-items: center;
    gap: 5px;
    cursor: pointer;

`;

const Hr = styled.hr`
    margin: 15px 0px;
    border: 0.5px solid ${({ theme }) => theme.soft};
`;

const CreatorContainer = styled.div`
    display: flex;
`;

const CreatorImage = styled.img`
    width: 36px;
    height:36px;
    border-radius: 50%;
    background-color: #999999;
`;

const CreatorInfo = styled.div`
    flex: 8;
`;

const Subscribe = styled.button`
    flex: 1;
    border: 2px solid #eb344f;
    background-color: #eb344f;
    color: white;
`;


const VideoCard = () => {
    return (
        <Container>
            <Wrapper>
                <VideoPlayer>
                    <VideoImage />
                </VideoPlayer>
                <Title> My First Video</Title>
                <Details>
                    <Info>1M views     1 day ago</Info>
                    <ButtonsGroup>
                        <Button>
                            <ThumbUpOutlinedIcon />
                            324
                        </Button>
                        <Button>
                            <ThumbDownOffAltOutlinedIcon />
                            Dislike
                        </Button>
                        <Button>
                            <ReplyOutlinedIcon />
                            Share
                        </Button>
                        <Button>
                            <AddTaskOutlinedIcon />
                            Save
                        </Button>
                    </ButtonsGroup>
                </Details>
                <Hr />
                <CreatorContainer>
                    <CreatorImage />
                    <CreatorInfo>
                        This is Your Favourite Channel. Welcome Guys. Enjoy .//.This is Your Favourite Channel. Welcome Guys. Enjoy ....
                        This is Your Favourite Channel. Welcome Guys. Enjoy ....
                        This is Your Favourite Channel. Welcome Guys. Enjoy ....
                    </CreatorInfo>
                    <Subscribe>
                        Subscribe
                    </Subscribe>
                </CreatorContainer>

            </Wrapper>
            <Recommendation>
                <h1>hello</h1>
            </Recommendation>
        </Container>
    )
}

export default VideoCard
